    .we(we),
    .spo(spo)); // Bus [7 : 0]

 
 
-- The following code must appear in the VHDL architecture header:
 
component block_mem
    port (
    a: IN std_logic_VECTOR(5 downto 0);
    d: IN std_logic_VECTOR(7 downto 0);
    clk: IN std_logic;
    we: IN std_logic;
    spo: OUT std_logic_VECTOR(7 downto 0));
end component;


 
